mapper.select:
select

,

from project_flows
where flow_id = #{flow_id,jdbcType=VARCHAR}
and project_id = #{project_id,jdbcType=INTEGER}
and version = #{version,jdbcType=INTEGER}

mapper.delete:
delete from project_flows
where flow_id = #{flow_id,jdbcType=VARCHAR}
and project_id = #{project_id,jdbcType=INTEGER}
and version = #{version,jdbcType=INTEGER}

mapper.insert:
insert into project_flows (flow_id, project_id, version,
modified_time, encoding_type, json
)
values (#{flow_id,jdbcType=VARCHAR}, #{project_id,jdbcType=INTEGER},
#{version,jdbcType=INTEGER},
#{modified_time,jdbcType=BIGINT}, #{encoding_type,jdbcType=TINYINT}, #{json,jdbcType=LONGVARBINARY}
)

mapper.update:
update project_flows


		modified_time = #{modified_time,jdbcType=BIGINT},


		encoding_type = #{encoding_type,jdbcType=TINYINT},


		json = #{json,jdbcType=LONGVARBINARY},


where flow_id = #{flow_id,jdbcType=VARCHAR}
and project_id = #{project_id,jdbcType=INTEGER}
and version = #{version,jdbcType=INTEGER}

update project_flows
set modified_time = #{modified_time,jdbcType=BIGINT},
encoding_type = #{encoding_type,jdbcType=TINYINT},
json = #{json,jdbcType=LONGVARBINARY}
where flow_id = #{flow_id,jdbcType=VARCHAR}
and project_id = #{project_id,jdbcType=INTEGER}
and version = #{version,jdbcType=INTEGER}

update project_flows
set modified_time = #{modified_time,jdbcType=BIGINT},
encoding_type = #{encoding_type,jdbcType=TINYINT}
where flow_id = #{flow_id,jdbcType=VARCHAR}
and project_id = #{project_id,jdbcType=INTEGER}
and version = #{version,jdbcType=INTEGER}
